<script setup>
const logoRef = ref(null);
const globalStore = useGlobalStore();

onMounted(() => {
  //sync loading logo with intro logo
  const observer = new MutationObserver((mutationsList) => {

    for (const mutation of mutationsList) {
      if (mutation.attributeName === 'style') {
        const currentDisplay = mutation.target.style.visibility;
        if (currentDisplay === 'inherit') {
          if (globalStore.showLoading) globalStore.showLoadingAction(false)
        }
      }
    }
  });

  observer.observe(logoRef.value, {
    attributes: true,
    attributeFilter: ['style'],
  });

  logoRef.value._observer = observer;
});

onUnmounted(() => {
  try {
    if (logoRef.value._observer) {
      logoRef.value._observer.disconnect();
    }

  } catch (e) {
    //
  }
});
</script>

